import { _saveQuestion, _saveQuestionAnswer } from "../utils/_DATA";
import { updateUserAnswers, updateUserQuestions } from "./users";

export const RECEIVE_QUESTIONS = "RECEIVE_QUESTIONS";
export const ADD_QUESTION = "ADD_QUESTION";
export const ADD_ANSWER = "ADD_ANSWER";

export function receiveQuestions(questions) {
	return {
		type: RECEIVE_QUESTIONS,
		questions,
	};
}

function addQuestion(question) {
	return {
		type: ADD_QUESTION,
		question,
	};
}

const saveQuestion = async (questionInfo) => {
    try {
        const response = await fetch("http://127.0.0.1:8000/api/createQuestion", {
            method: "POST",
            headers: {
                Accept: "application/json",
                "Content-Type": "application/json",
            },
            body: JSON.stringify({
                user_id: questionInfo.authedUser,
                optionOneText: questionInfo.optionOneText,
                optionTwoText: questionInfo.optionOneText,
            }),
        });

        if (!response.ok) {
            console.log("error");
            throw new Error(`Error: Status ${response.status}`);
        }
        const result = await response.json();
        console.log(result)
    } catch (error) {
        return alert("Error occured");
    }
}

export function handleAddQuestion(optionOneText, optionTwoText) {
	return async (dispatch, getState) => {
		const { authedUser } = getState();

		return saveQuestion({
			optionOneText,
			optionTwoText,
			authedUser,
		}).then((question) => {
            // console.log(question.json());
			// dispatch(addQuestion({optionOneText, optionTwoText, authedUser}));
			// dispatch(updateUserQuestions(question.id, authedUser));
		});
	};
}

function addAnswer(answer) {
	return {
		type: ADD_ANSWER,
		answer,
	};
}

const saveAnswer = async (answerInfo) => {
    try {
        const response = await fetch("http://127.0.0.1:8000/api/addVote", {
            method: "POST",
            headers: {
                Accept: "application/json",
                "Content-Type": "application/json",
            },
            body: JSON.stringify({
                question_id: answerInfo.qid,
                user_id: answerInfo.authedUser,
                option: answerInfo.answer,
            }),
        });

        if (!response.ok) {
            throw new Error(`Error: Status ${response.status}`);
        }
    } catch (error) {
        return alert("Error occured");
    }
}

export function handleAddAnswer(answerInfo) {
	return async (dispatch) => {
		return saveAnswer(answerInfo).then(() => {
            dispatch(addAnswer(answerInfo));
            dispatch(updateUserAnswers(answerInfo));
        });
	};
}
