import { SET_AUTHED_USER, SET_DEFAULT_USER } from "../actions/authedUser";
import { LOG_OUT_USER } from "../actions/authedUser";

export default function authedUser(state = null, action) {
	switch (action.type) {
		case SET_AUTHED_USER:
			return action.id;
		case LOG_OUT_USER:
			return null;
		default:
			return state;
	}
}
