import { _getUsers } from "../utils/_DATA";
import { _getQuestions } from "../utils/_DATA";
import { receiveUsers } from "./users";
import { receiveQuestions } from "./questions";

const getUsers = async () => {
	try {
		const response = await fetch("http://127.0.0.1:8000/api/getUsers");

		if (!response.ok) {
			throw new Error(`Error! status: ${response.status}`);
		}

		const result = await response.json();
		return result;
	} catch (error) {
		console.log(error);
	}
};

const getQuestions = async () => {
	try {
		const response = await fetch("http://127.0.0.1:8000/api/getQuestions");

		if (!response.ok) {
			throw new Error(`Error! status: ${response.status}`);
		}

		const result = await response.json();
		return result;
	} catch (error) {
		console.log(error);
	}
};

export default function handleInitialData() {
	return async (dispatch) => {
		const users = await _getUsers().then((res) => (res));
		const questions = await _getQuestions().then((res) => (res));
		// const users = await getUsers().then((res) => (res));
		// const questions = await getQuestions();
		dispatch(receiveUsers(users));
		dispatch(receiveQuestions(questions));
	};
}
